import styled from 'styled-components';

export const ProfileCard = styled.div({
  margin: 'auto',
  width: 400,
  boxShadow: '0 0 5px 5px #cecdcd',
  borderRadius: 5,
  textAlign: 'center',
  padding: '40px 0',
});

export const Description = styled.div({
  padding: 40,
  background: ' #fff',
});

export const ProfileImg = styled.img({
  marginBottom: 30,
  height: 50,
  width: 50,
  borderRadius: '50%',
  margin: 'auto',
});

export const Name = styled.p({
  color: '#000',
  fontSize: 24,
  paddingBottom: 10,
});

export const Tag = styled.p({
  color: '#eee',
  fontSize: 16,
  paddingBottom: 10,
});

export const Location = styled.p({
  color: '#eee',
  fontSize: 16,
  paddingBottom: 10,
});
