import styled from 'styled-components';

export const ProfileCard = styled.div({
  background: '#fff',
  margin: 'auto',
  width: 400,
  boxShadow: '0 0 5px 5px #aaaaaa',
  borderRadius: 5,
});

export const Description = styled.div({
  padding: 40,
  background: ' #fff,',
});
