import styled from 'styled-components';

export const StatisticsSection = styled.div`
  width: 400px;
  border: 1px solid #5f5f5f;
  border-radius: 5px;
  text-align: center;
  margin: auto;
  box-shadow: 0 -2px 5px 2px #afaeae;
`;

export const StatsTitle = styled.h2`
  text-transform: uppercase;
  font-size: 18px;
  padding: 30px;
`;
