  StatisticsSection,
  StatsItem,
  StatsLabel,
  StatsList,
  StatsPer,
  StatsTitle,
} from './Statistics.styled';

export const Statistics = ({ stats }) => {
  return (
    <StatisticsSection>
      <StatsTitle>Upload stats</StatsTitle>

      <StatsList>
        {stats.map(({ id, label, percentage }) => {
          return (
            <StatsItem key={id}>
              <StatsLabel>{label}</StatsLabel>
              <StatsPer>{percentage}</StatsPer>
