import styled from 'styled-components';

export const Table = styled.table`
  width: 400px;
  margin: auto;
  box-shadow: 0 -2px 5px 2px #afaeae;
  border-radius: 3px;
`;

export const THead = styled.thead`
  background: teal;
`;

export const TrHead = styled.tr;
